    public static int getPrimarySimIcon(boolean roaming, int subId) {
        int defaultDataSubId = SubscriptionManager.getDefaultDataSubId();
        if (roaming) {
            if (subId == defaultDataSubId) {
                return R.drawable.stat_sys_data_primary_simcard_roam;
            }
            return R.drawable.stat_sys_data_fully_connected_roam;
        } else if (subId == defaultDataSubId) {
            return R.drawable.stat_sys_data_primary_simcard;
        } else {
            return 0;
        }
    }
